import { useState } from 'react';

function Square () {

  const [value, setValue] = useState(null);

  function handleClick() {
    console.log ("hello there!");
    setValue ('X');
  }

  return(
    <button
      className="square"
      onClick={handleClick}
    >
      {value}
    </button>
  );
}

